import React, { useState, useEffect } from 'react';
import { ecoSuggestions } from './ecoSuggestions';

const API = 'http://localhost:4000/api';

function FootprintForm({ username, onSubmit }) {
  const [travel, setTravel] = useState('');
  const [shopping, setShopping] = useState('');
  const [diet, setDiet] = useState('omnivore');

  return (
    <form onSubmit={e => {
      e.preventDefault();
      onSubmit({ travel: Number(travel), shopping: Number(shopping), diet });
    }}>
      <h3>Enter today's habits</h3>
      <label>
        Travel (km by car): <input type="number" value={travel} onChange={e => setTravel(e.target.value)} />
      </label>
      <br />
      <label>
        Shopping ($ spent): <input type="number" value={shopping} onChange={e => setShopping(e.target.value)} />
      </label>
      <br />
      <label>
        Diet:
        <select value={diet} onChange={e => setDiet(e.target.value)}>
          <option value="omnivore">Omnivore</option>
          <option value="vegetarian">Vegetarian</option>
          <option value="vegan">Vegan</option>
        </select>
      </label>
      <br />
      <button type="submit">Submit</button>
    </form>
  );
}

function Suggestions() {
  return (
    <div>
      <h3>Eco Suggestions</h3>
      <ul>
        {ecoSuggestions.travel.map((suggestion, i) => (
          <li key={i}>{suggestion}</li>
        ))}
        {ecoSuggestions.shopping.map((suggestion, i) => (
          <li key={i}>{suggestion}</li>
        ))}
        {ecoSuggestions.diet.map((suggestion, i) => (
          <li key={i}>{suggestion}</li>
        ))}
      </ul>
    </div>
  );
}

function App() {
  const [username, setUsername] = useState('');
  const [submitted, setSubmitted] = useState(false);

  return (
    <div>
      <h1>Carbon Footprint Tracker</h1>
      {!submitted ? (
        <form onSubmit={e => {
          e.preventDefault();
          setSubmitted(true);
        }}>
          <label>
            Username: <input value={username} onChange={e => setUsername(e.target.value)} />
          </label>
          <button type="submit">Start</button>
        </form>
      ) : (
        <>
          <FootprintForm username={username} onSubmit={() => {}} />
          <Suggestions />
        </>
      )}
    </div>
  );
}

export default App;
